mapper.select:
select * from (select row_.*, rownum rownum_ from (select * from
cwgl_department order by dep_id desc ) row_

	rownum <=#{size} and dep_is_delete=0

)

	rownum_ >=#{page}

select * from (select row_.*, rownum rownum_ from (select * from
cwgl_department order by dep_id asc ) row_

	rownum <=#{size} and (dep_id=#{dep_id} or dep_parentid=#{dep_id}) and dep_is_delete=0

)

	rownum_ >=#{page}
